package main

var input = ``

func main() {
	println(marker(4))
	println(marker(14))
}

func marker(n int) int {
	bytes := []byte(input)
	for i := range bytes {
		window := bytes[i : i+n]
		if uniq(window) {
			return i + n
		}
	}

	panic("not found")
}

func uniq(items []byte) bool {
	seen := map[byte]bool{}
	for _, b := range items {
		if seen[b] {
			return false
		}

		seen[b] = true
	}

	return true
}
