package main

var input = ``

func main() {
	println(FindMarker([]byte(input), 4))
	println(FindMarker([]byte(input), 14))
}

func FindMarker(input []byte, n int) int {
	for i := range input {
		window := input[i : i+n]
		if uniq(window) {
			return i + n
		}
	}

	panic("not found")
}

func uniq(items []byte) bool {
	seen := map[byte]bool{}
	for _, b := range items {
		if seen[b] {
			return false
		}

		seen[b] = true
	}

	return true
}
