package main

import "golang.org/x/exp/slices"

var input = ``

func main() {
	println(FindMarker([]byte(input), 4))
	println(FindMarker([]byte(input), 14))
}

func FindMarker(input []byte, n int) int {
	for i := range input {
		window := input[i : i+n]
		if !hasDupe(window) {
			return i + n
		}
	}

	panic("not found")
}

func hasDupe(items []byte) bool {
	for i, b := range items {
		if slices.Contains(items[i+1:], b) {
			return true
		}
	}

	return false
}
